import React from "react";
import { useParams } from "react-router-dom";

const PostDetail = (props) => {
  const { posts } = props;
  const { postId } = useParams();
  const singlePost = posts.find((onePost) => {
    const foundPost = onePost._id == postId;
    return foundPost;
  });

  return (
    <div className="posts-container">
      <div className="ui card">
        <div className="content">
          <div className="left floated aligned header">
            <h1>{posts.title}</h1>
          </div>
          <div className="right floated aligned header">
            {posts.isAuthor ? <span>MINE</span> : null}
          </div>

          <p className="description">{posts.description}</p>

          <p className="center aligned extra content">{posts.price}</p>
          <p className="center aligned extra content">
            {posts.author.username}
          </p>
          <p className="center aligned extra content">{posts.location}</p>
        </div>
      </div>
    </div>
  );
};

export default PostDetail;
