import React from "react";
import { Link } from "react-router-dom";
import { deleteVacation } from "../api/api";
import "./Posts.css";

const handleDeleteClick = async (postId) => {
  await deletePost(token, postId);
  setPosts((prevPosts) => prevPosts.filter((posts) => post.id !== postId));
};

const Posts = ({ posts, setPosts, tokens }) => {
  return (
    <>
      <header>
        <h1>Posts</h1>
      </header>
      <button type="button" className="ui button" onClick={() => {}}>
        Create Post
      </button>
      <Link to="/posts/create" className="ui button">
        Create Post
      </Link>
      <section>
        {posts.map((posts) => {
          return (
            <>
              <div className="posts-container">
                <div key={posts._id} className="ui card">
                  <div className="content">
                    <div className="center aligned header">
                      <h1>{posts.title}</h1>
                    </div>

                    <p className="description">{posts.description}</p>

                    <p className="center aligned extra content">
                      {posts.price}
                    </p>
                    <p className="center aligned extra content">
                      {posts.author.username}
                    </p>
                    <p className="center aligned extra content">
                      {posts.location}
                    </p>
                  </div>
                </div>
              </div>
            </>
          );
        })}
      </section>
    </>
  );
};

export default Posts;
