import React, { useState } from "react";
import { Link } from "react-router-dom";

const Profile = (props) => {
  const { token, posts, getPosts, username, filteredPosts } = props;

  return (
    <>
      <header>
        <h1>Profile</h1>
      </header>
      <section>
        {filteredPosts.map((posts) => {
          return (
            <>
              <div className="posts-container" key={posts._id}>
                <div className="ui card">
                  <div className="content">
                    <div className="left floated aligned header">
                      <h1>{posts.title}</h1>
                    </div>
                    <div className="right floated aligned header">
                      {posts.isAuthor ? <span>MINE</span> : null}
                    </div>

                    <p className="description">{posts.description}</p>

                    <p className="center aligned extra content">
                      {posts.price}
                    </p>
                    <p className="center aligned extra content">
                      {posts.author.username}
                    </p>
                    <p className="center aligned extra content">
                      {posts.location}
                    </p>
                  </div>
                  <div className="center aligned header">
                    <Link to={`/posts/${posts._id}`}>View Post</Link>
                  </div>
                  {posts.isAuthor && token ? (
                    <button
                      className="negative ui button left floated"
                      onClick={() => {
                        handleDeleteClick(posts._id);
                      }}
                    >
                      Delete
                    </button>
                  ) : null}

                  <div
                    role="list"
                    className="ui divided relaxed list"
                    style={{ color: "#444", clear: "both" }}
                  >
                    {posts.messages.map((messages) => {
                      return (
                        <div
                          role="listitem"
                          className="item"
                          key={messages._id}
                        >
                          {messages.fromUser.username !== username ? (
                            <>
                              <b>{messages.fromUser.username} </b>
                              <p className="content">{messages.content}</p>
                            </>
                          ) : null}
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            </>
          );
        })}
      </section>
    </>
  );
};

export default Profile;
